import React, { useRef, useState } from "react";
import Anchor from "../../components/Anchor";
import OAuth from "../../components/Buttons/OAuth";
import Primary from "../../components/Buttons/Primary";
import Heading from "../../components/Heading";
import InputHandler from "../../components/InputHandler";
import facebook from "../../assets/icons/facebook.png";
import google from "../../assets/icons/google.png";

const SignIn = () => {
  const [formData, setFormData] = useState({});
  const formRef = useRef();

  const submitHandler = (e) => {
    e.preventDefault();
    setFormData({
      email: formRef.current.email.value,
      password: formRef.current.password.value,
    });

    console.log(formData);
  };

  return (
    <form ref={formRef}>
      <Heading>Login to OwnIt</Heading>
      <InputHandler placeholder="Email" type="email" name="email" />
      <InputHandler placeholder="Password" type="password" name="password" />
      <Anchor>Forgot Password?</Anchor>
      <Primary onClick={submitHandler}>Login</Primary>
      <div>
        New to OwnIt: <Anchor>SignUp</Anchor>
      </div>
      <div>
        <OAuth img={google}>Google</OAuth>
        <OAuth img={facebook}>Facebook</OAuth>
      </div>
    </form>
  );
};

export default SignIn;
